INSERT INTO book (id, author, description, discount, image, isbn, link, price, pubdate, publisher, title, created_date, modified_date) VALUES (4, '최범균', 'DDD의 핵심 개념을 배우고 구현으로 익히기!이 책은 DDD(도메인 주도 설계)를 처음 접하는 개발자를 위한 책이다. DDD를 실제 업무에 적용할 수 있도록 기본적인 이론을 설명하고 이를 구현한 코드를 바탕으로 입문자가 헤매지 않고 DDD를 학습할 수 있도록 했다.
INSERT INTO book (id, author, description, discount, image, isbn, link, price, pubdate, publisher, title, created_date, modified_date) VALUES (5, '톰 홈버그', '우리 모두는 낮은 개발 비용으로 유연하고 적응이 쉬운 소프트웨어 아키텍처를 구축하고자 한다. 그러나 불합리한 기한과 쉬워보이는 지름길은 이러한 아키텍처를 구축하는 것을 매우 어렵게 만든다.이 책에서는 전통적인 계층형 아키텍처(LAYERED ARCHITECTURE) 스타일과 이 스타일의 단점을 논하는... ', '16200', 'https://bookthumb-phinf.pstatic.net/cover/212/940/21294056.jpg?type=m1&udate=20211118', '1158392753 9791158392758', 'http://book.naver.com/bookdb/book_detail.php?bid=21294056', '18000', '20211126', '위키북스', '클린 아키텍처 (자바 코드로 구현하는 클린 웹 애플리케이션,만들면서 배우는)', '2022-02-18 15:02:41.363028', '2022-02-18 15:02:41.363028');
INSERT INTO book (id, author, description, discount, image, isbn, link, price, pubdate, publisher, title, created_date, modified_date) VALUES (6, '전영표', '문화매체의 총아라 할 출판 산업이 오늘의 정보사회를 맞이하면서 그 존립이 위태로워지고 있다. 금속활자에 의한 인쇄출판문화가 우리 사회에 등장한 이래 6백년이 넘게 이용해 온 역사가 갑자기 전자라는 새로운 정보매체에 그 자리를 물려줘야 할 전환적 시대를 맞이하고 있다.', '17390', 'https://bookthumb-phinf.pstatic.net/cover/015/178/01517825.jpg?type=m1&udate=20141122', '8955650981 9788955650983', 'http://book.naver.com/bookdb/book_detail.php?bid=1517825', '18500', '20050301', '청목출판사', '출판정보미디어론', '2022-02-19 14:54:26.092461', '2022-02-19 14:54:26.092461');
